


  CREATE OR REPLACE FORCE VIEW "XDMADM"."VW_SLS_TRTRY_MAP" ("DIV_NBR", "TRADE_CLASS", "TRTRY_CD", "TRTRY_TYPE", "DSTRCT_CD", "DSTRCT_NM", "DSTRCT_SLS_MGR_NM", "DSTRCT_SLS_MGR_ID", "DSTRCT_SLS_MGR_NM_ID", "TRTRY_MGR_ID", "TRTRY_MGR_NM", "TRTRY_MGR_NM_ID", "RGN_SLS_MGR_ID", "RGN_SLS_MGR_NM", "RGN_SLS_MGR_NM_ID") AS
  SELECT DISTINCT
          sm.div_nbr,
          '1' trade_class,
          sm.trtry_cd,
          sm.trtry_type,
          sm.dstrct_cd,
          sm.dstrct_nm,
          sm.dstrct_sls_mgr_nm,
          sm.dstrct_sls_mgr_id,
          (CASE
              WHEN sm.dstrct_sls_mgr_id IS NULL
              THEN
                 NULL
              ELSE
                 sm.dstrct_sls_mgr_nm || ' (' || sm.dstrct_sls_mgr_id || ')'
           END)
             dstrct_sls_mgr_nm_id,
          sm.trtry_mgr_id,
          sm.trtry_mgr_nm,
          (CASE
              WHEN sm.trtry_mgr_id IS NULL THEN NULL
              ELSE sm.trtry_mgr_nm || ' (' || sm.trtry_mgr_id || ')'
           END)
             trtry_mgr_nm_id,
          sm.rgn_sls_mgr_id,
          sm.rgn_sls_mgr_nm,
          (CASE
              WHEN sm.rgn_sls_mgr_id IS NULL THEN '(NOT APPLICABLE)'
              ELSE sm.rgn_sls_mgr_nm || ' (' || sm.rgn_sls_mgr_id || ')'
           END)
             rgn_sls_mgr_nm_id
     FROM xdmadm.sls_trtry_map sm
   UNION
   SELECT DISTINCT
          sd.div_nbr,
          '1' trade_class,
          sd.trtry_cd,
          sd.trtry_type,
          sd.dstrct_cd,
          sd.dstrct_nm,
          sd.dstrct_sls_mgr_nm,
          sd.dstrct_sls_mgr_id,
          (CASE
              WHEN sd.dstrct_sls_mgr_id IS NULL
              THEN
                 NULL
              ELSE
                 sd.dstrct_sls_mgr_nm || ' (' || sd.dstrct_sls_mgr_id || ')'
           END)
             dstrct_sls_mgr_nm_id,
          sd.trtry_mgr_id,
          sd.trtry_mgr_nm,
          (CASE
              WHEN sd.trtry_mgr_id IS NULL THEN NULL
              ELSE sd.trtry_mgr_nm || ' (' || sd.trtry_mgr_id || ')'
           END)
             trtry_mgr_nm_id,
          sd.rgn_sls_mgr_id,
          sd.rgn_sls_mgr_nm,
          (CASE
              WHEN sd.rgn_sls_mgr_id IS NULL THEN '(NOT APPLICABLE)'
              ELSE sd.rgn_sls_mgr_nm || ' (' || sd.rgn_sls_mgr_id || ')'
           END)
             rgn_sls_mgr_nm_id
     FROM xdmadm.sls_trtry_map_dummy sd;




