  CREATE OR REPLACE FORCE VIEW "XDMADM"."VW_PROD_RECALL_CUST_CNTCT" ("DIV_NBR", "CUST_NBR", "RECALL_EMAIL_ADDR", "RECALL_FRST_NM", "RECALL_LST_NM", "RECALL_JOB_TITLE", "RECALL_PHONE", "RECALL_MOBILE", "ESCALATION_1_EMAIL_ADDR", "ESCALATION_1_FRST_NM", "ESCALATION_1_LST_NM", "ESCALATION_1_JOB_TITLE", "ESCALATION_1_PHONE", "ESCALATION_1_MOBILE", "ESCALATION_2_EMAIL_ADDR", "ESCALATION_2_FRST_NM", "ESCALATION_2_LST_NM", "ESCALATION_2_JOB_TITLE", "ESCALATION_2_PHONE", "ESCALATION_2_MOBILE") AS
  SELECT DIV_NBR,
          CUST_NBR,
          RECALL_EMAIL_ADDR,
          RECALL_FRST_NM,
          RECALL_LST_NM,
          RECALL_JOB_TITLE,
          RECALL_PHONE,
          RECALL_MOBILE,
          ESCALATION_1_EMAIL_ADDR,
          ESCALATION_1_FRST_NM,
          ESCALATION_1_LST_NM,
          ESCALATION_1_JOB_TITLE,
          ESCALATION_1_PHONE,
          ESCALATION_1_MOBILE,
          ESCALATION_2_EMAIL_ADDR,
          ESCALATION_2_FRST_NM,
          ESCALATION_2_LST_NM,
          ESCALATION_2_JOB_TITLE,
          ESCALATION_2_PHONE,
          ESCALATION_2_MOBILE
     FROM (SELECT div_nbr recall_div_nbr,
                  cust_nbr recall_cust_nbr,  --cntct_ctgry recall_cntct_ctgry,
                  EMAIL_ADDR recall_EMAIL_ADDR,
                  FRST_NM recall_FRST_NM,
                  LST_NM recall_LST_NM,
                  JOB_TITLE recall_JOB_TITLE,
                  PHONE recall_PHONE,
                  MOBILE recall_MOBILE
             FROM (SELECT div_nbr,
                          cust_nbr,
                          CNTCT_CTGRY,
                          EMAIL_ADDR,
                          FRST_NM,
                          LST_NM,
                          JOB_TITLE,
                          PHONE,
                          MOBILE,
                          RANK ()
                          OVER (PARTITION BY div_nbr, cust_nbr
                                ORDER BY cntct_id DESC, accnt_id DESC)
                             rnk
                     FROM xdmadm.cust_cntct a
                    WHERE cntct_status = 'Active' AND CNTCT_CTGRY = 'Recall')
            WHERE rnk = 1) rcl,
          (SELECT div_nbr Escalation_1_div_nbr,
                  cust_nbr Escalation_1_cust_nbr, -- cntct_ctgry Escalation_1_cntct_ctgry,
                  EMAIL_ADDR Escalation_1_EMAIL_ADDR,
                  FRST_NM Escalation_1_FRST_NM,
                  LST_NM Escalation_1_LST_NM,
                  JOB_TITLE Escalation_1_JOB_TITLE,
                  PHONE Escalation_1_PHONE,
                  MOBILE Escalation_1_MOBILE
             FROM (SELECT div_nbr,
                          cust_nbr,
                          CNTCT_CTGRY,
                          EMAIL_ADDR,
                          FRST_NM,
                          LST_NM,
                          JOB_TITLE,
                          PHONE,
                          MOBILE,
                          RANK ()
                          OVER (PARTITION BY div_nbr, cust_nbr
                                ORDER BY cntct_id DESC, accnt_id DESC)
                             rnk
                     FROM xdmadm.cust_cntct a
                    WHERE     cntct_status = 'Active'
                          AND CNTCT_CTGRY = 'Recall Escalation 1')
            WHERE rnk = 1) rcl1,
          (SELECT div_nbr Escalation_2_div_nbr,
                  cust_nbr Escalation_2_cust_nbr, --cntct_ctgry Escalation_2_cntct_ctgry,
                  EMAIL_ADDR Escalation_2_EMAIL_ADDR,
                  FRST_NM Escalation_2_FRST_NM,
                  LST_NM Escalation_2_LST_NM,
                  JOB_TITLE Escalation_2_JOB_TITLE,
                  PHONE Escalation_2_PHONE,
                  MOBILE Escalation_2_MOBILE
             FROM (SELECT div_nbr,
                          cust_nbr,
                          CNTCT_CTGRY,
                          EMAIL_ADDR,
                          FRST_NM,
                          LST_NM,
                          JOB_TITLE,
                          PHONE,
                          MOBILE,
                          RANK ()
                          OVER (PARTITION BY div_nbr, cust_nbr
                                ORDER BY cntct_id DESC, accnt_id DESC)
                             rnk
                     FROM xdmadm.cust_cntct a
                    WHERE     cntct_status = 'Active'
                          AND CNTCT_CTGRY = 'Recall Escalation 2')
            WHERE rnk = 1) rcl2,
          (SELECT DISTINCT div_nbr, cust_nbr
             FROM xdmadm.cust_cntct
            WHERE cntct_status = 'Active' AND cntct_ctgry IS NOT NULL) org
    WHERE     org.div_nbr = rcl.recall_div_nbr(+)
          AND org.cust_nbr = rcl.recall_cust_nbr(+)
          AND org.div_nbr = rcl1.Escalation_1_div_nbr(+)
          AND org.cust_nbr = rcl1.Escalation_1_cust_nbr(+)
          AND org.div_nbr = rcl2.Escalation_2_div_nbr(+)
          AND org.cust_nbr = rcl2.Escalation_2_cust_nbr(+);




