  CREATE OR REPLACE FORCE VIEW "XDMADM"."VW_FACT_CUST_CORP" ("CUST_NBR", "DIV_NBR", "PROD_NBR", "PIM_USF_STD_PROD_CD", "CRM_MP_FBD_PRCH_OVRD_IND", "CRM_TTL_WKLY_PTNL", "MP_FOOD_BVRG_DSPSBL_PRCH", "AVG_MTHLY_PTNL", "LAST_RT_TRANS_DT", "AVG_PNTRTN_RATE", "LAST_FISC_MTH_SOS_CNT") AS
  SELECT C.CUST_NBR,
          C.DIV_NBR,
          '99999999999' prod_nbr,
          0 pim_usf_std_prod_cd,
          C.CRM_MP_FBD_PRCH_OVRD_IND,
          C.CRM_TTL_WKLY_PTNL,
          CMC.MP_FOOD_BVRG_DSPSBL_PRCH,
          CASE
             WHEN C.CRM_MP_FBD_PRCH_OVRD_IND = 'N'
                  AND NVL (CMC.MP_FOOD_BVRG_DSPSBL_PRCH, 0) = 0
             THEN
                (C.CRM_TTL_WKLY_PTNL * 52) / 12
             WHEN C.CRM_MP_FBD_PRCH_OVRD_IND = 'N'
                  AND NVL (CMC.MP_FOOD_BVRG_DSPSBL_PRCH, 0) <> 0
             THEN
                CMC.MP_FOOD_BVRG_DSPSBL_PRCH / 12
             WHEN C.CRM_MP_FBD_PRCH_OVRD_IND = 'Y'
                  AND NVL (C.CRM_TTL_WKLY_PTNL, 0) = 0
             THEN
                CMC.MP_FOOD_BVRG_DSPSBL_PRCH / 12
             WHEN C.CRM_MP_FBD_PRCH_OVRD_IND = 'Y'
                  AND NVL (C.CRM_TTL_WKLY_PTNL, 0) <> 0
             THEN
                (C.CRM_TTL_WKLY_PTNL * 52) / 12
          END
             AS AVG_MTHLY_PTNL,
          C.LAST_RT_TRANS_DT,
          NVL (CPR.AVG_PNTRTN_RATE, 0.4),
          last_fisc_mth_sos_cnt
     FROM XDMADM.CUST_CORP C,
          XDMADM.CUST_MKTPLUS_CORP CMC,
          XDMADM.CUST_AVG_PNTRTN_RATE CPR
    WHERE     c.div_nbr = cmc.div_nbr(+)
          AND c.cust_nbr = cmc.cust_nbr(+)
          AND cmc.curr_ind(+) = 'Y'
          AND c.menu_typ_cd = cpr.menu_typ_cd(+);




